void up() // -- Funktion fÃ¼r hoch
{
  if (dir != -1)
  {
    if (TestMode) {
      Serial.println("Rollladen fÃ¤hrt hoch");
    }
      digitalWrite(D2, LOW);
      delay(300);
      digitalWrite(D2, HIGH);
      delay(500);
  }   
}

void down() // -- Funktion fÃ¼r runter
{ 
  if (dir != 1)
  {
    if (TestMode) {
      Serial.println("Rollladen fÃ¤hrt runter");
    }
      digitalWrite(D1, LOW);
      delay(300);
      digitalWrite(D1, HIGH);
      delay(500);
  }
}

void savemaxcount() // -- Speicherung der unteren Position
{
  if (MaxPos_MQTT  = "selected") {
    char cmaxcount[4];
    itoa( count, cmaxcount, 10 );
    mqttSend(mqttMaxPosTopic, cmaxcount, true);
    maxcount = count;
    if (TestMode) {
      Serial.print("Neue untere Position:");
      Serial.println(String(maxcount));
      Serial.println("auf MQTT-Server gespeichert");
    }
  } else {
    EEPROM.begin(4096);
    EEPROM.write (4000, count);
    EEPROM.commit();
    maxcount = EEPROM.read (4000);
    EEPROM.end();
    if (TestMode) {
      Serial.print("Neue untere Position:");
      Serial.println(String(maxcount));
      Serial.println("in EEPROM gespeichert");
    }
  }
  drivemaxcount = 0;
  digitalWrite(D2, LOW);
  delay(300);
  digitalWrite(D2, HIGH);
  delay(1000);
  digitalWrite(D2, LOW);
  delay(300);
  digitalWrite(D2, HIGH);
  delay(1000);
  digitalWrite(D1, LOW);
  delay(300);
  digitalWrite(D1, HIGH);
  delay(1000);
  digitalWrite(D1, LOW);
  delay(300);
  digitalWrite(D1, HIGH);
  delay(500);
}

