  // client.setServer(mqtt_server, mqtt_port);

  // -- Setze MQTT Callback-Funktion
  client.setCallback(callback);
  
  // -- Definiere Pins
  pinMode(D5, INPUT);         // ImpulszÃ¤hler
  pinMode(D3, INPUT_PULLUP);  // Motor fÃ¤hrt hoch
  pinMode(D4, INPUT_PULLUP);  // Motor fÃ¤hrt runter
  pinMode(D1, OUTPUT);        // Taste fÃ¼r runter
  digitalWrite(D1, HIGH);     // Setze Taste auf HIGH
  pinMode(D2, OUTPUT);        // Taste fÃ¼r hoch
  digitalWrite(D2, HIGH);     // Setze Taste auf HIGH
  pinMode(D6, INPUT_PULLUP);  // Test-Taste fÃ¼r Debug-Tests

